package paillier

import (
	"math/big"

	. "github.com/onsi/ginkgo"
	. "github.com/onsi/gomega"
)

var _ = Describe("Nosmallfactoezkproof test", func() {
	config := NewS256()
	p, _ := new(big.Int).SetString("", 10)
	q, _ := new(big.Int).SetString("", 10)
	n := new(big.Int).Mul(p, q)
	ssIDInfo := []byte("Mark HaHa")
	rho := []byte("Ian HaHa")
	pedp, _ := new(big.Int).SetString("", 10)
	pedq, _ := new(big.Int).SetString("", 10)
	pedN := new(big.Int).Mul(pedp, pedq)
	pedt := big.NewInt(9)
	peds := big.NewInt(729)
	Context("It is OK", func() {
		It("over Range, should be ok", func() {
			zkproof, err := NewNoSmallFactorMessage(config, ssIDInfo, rho, p, q, n, pedN, peds, pedt)
			Expect(err).Should(BeNil())
			err = zkproof.Verify(config, ssIDInfo, rho, n, pedN, peds, pedt)
			Expect(err).Should(BeNil())
		})
	})

})
